import React, {useState} from "react";

function App() {
const [item, setItem] = useState({
  lItem: ""
});

function handleChange(event) {
  const {name, value} = event.target;

  setContact(preValue => {
    if (name === "lItem") {
      return {
        lItem: value, 
      }; 
    } else if (name === "lName") { 
      return {
        fName: preValue.fName,
        lName: value,
        email: preValue.email 
      };  
    } else if (name === "email") {
      return {
        fName: preValue.fName,
        lName: preValue.lName,
        email: value
      };
    }
  });
}

  return (
    <div className="container">
      <div className="heading">
        <h1>To-Do List</h1>
      </div>
      <div className="form">
        <input type="text" />
        <button>
          <span>Add</span>
        </button>
      </div>
      <div>
        <ul>
          <li>A Item</li>
          <li>{name}</li>
        </ul>
      </div>
    </div>
  );
}

export default App;
